package net.minecraft.src;import modoptionsapi.ModOptionsAPI;import modoptionsapi.ModBooleanOption;/*** Mushroom Item Representation for varied mushrooms.** @author	Clinton Alexander* @version	1.0.0.0*/public class ItemMushroom extends ItemPlantable implements Plantable {	    public ItemMushroom(int i)    {        super(i);    }    public int func_21012_a(int i)    {        return i;    }		/**	* Check if the item can be planted on top of the 	* block with the idBelow at i, j, k	*	* @param	belowID		ID of block below	* @param	age			Age of item	* @return 	True when plantable	*/	public boolean plantable(World world, int i, int j, int k, int belowID, int age) {		boolean shroomGrow 	= ((ModBooleanOption) ModOptionsAPI.getModOptions(mod_AutoForest.MENU_NAME).				getSubOption(mod_AutoForest.PLANT_MENU_NAME).				getSubOption(mod_AutoForest.SHROOMS_MENU_NAME).				getOption("ShroomsGrow")).getValue();		return ((shroomGrow) && (shroomPlant(belowID)));	}		/**	* Check if the current plant is a shroom and whether it should plant	* Shrooms can only not plant on grass or in daylight	*	* @param	belowID		ID of below block	* @return	true if can plant and is shroom	*/	private boolean shroomPlant(int belowID) {		return ((belowID != Block.glass.blockID) && (belowID != Block.ice.blockID));	}}